	(6, 1, 1, 1, 'Promover desórdenes y destrucción dentro y fuera del aula; en los dormitorios, comedor, biblioteca, áreas productivas, centros de práctica, de investigación y vinculación', FALSE, 2),
	(7, 1, 1, 1, 'Realizar o participar en acciones que deterioran el medio ambiente dentro y fuera del campus universitario', FALSE, 2),
	(8, 1, 1, 1, 'Colocar afiches en áreas de uso común, con motivos pornográficos, que inciten a la violencia, cultos satánicos, o que denigren la honorabilidad de las personas, etc.', FALSE, 2),
	(9, 1, 1, 1, 'Permanecer fuera del campus, después de las 7:00 PM, excepto con permiso para asuntos religiosos, deportivos, artísticos y culturales.', FALSE, 2),
